import { useEffect, useState } from "react";
import $ from "jquery";

import Button from "./Components/Button/Button";
import "./Main.css";

// NUMEROS
// https://makemeapassword.ligos.net/api/v1/pin/json?c=10&l=6
// NUMEROS Y LETRAS
// https://makemeapassword.ligos.net/api/v1/alphanumeric/json?c=10&l=12
// "num" = numeros
// "char" = caracteres
// "caps" = mayusculas
// https://passwordinator.herokuapp.com/?num=true&char=true&caps=true&len=18

function App() {
	const [rangoMaximo, setRangoMaximo] = useState();

	const generarClave = ( restriccion ) => {
		const num = "&num=true";
		const char = "&char=true";
		const caps = "&caps=true";
		const api = `https://passwordinator.herokuapp.com/${num}${char}${caps}&len=18`;
		Promise.all([
			fetch( api ).then((value) => value.json())
		])
			.then((value) => {
				const clave = value[0].data;
				console.log( `Password => ${clave}` );
			});
	};

	const verificarRestricciones = () => {
		// $.each($(".restricciones > label"), function () {
		// 	const rango = $(".range").val();
		// 	const restriccion = $(this).text();
		// 	const isCheck = $(this).find("input").is(":checked");
		// 	if ( isCheck ) {
		// 		const tru = $(this).attr("for");
		// 		console.log( `El ${tru} esta TRUE` );
		// 	}
		// 	const num = "false";
		// 	// console.log( `
		// 	// 	Restriccion: ${restriccion}
		// 	// 	Check?: ${isCheck}
		// 	// ` );
		// });
		const num = function () {
			const num = $("label[for=num] input").val();
			return num;
		};
		/*
		const num = "&num=true";
		const char = "&char=true";
		const caps = "&caps=true";
		*/
		const url = `https://passwordinator.herokuapp.com/${num}${char}${caps}&len=18`;
		console.log( `URL -> ${url}` );
		// console.log(`Rango: ${rango}`);
	};

	const eselrangoMaximo = () => {
		const elrango = $("#range").val();
		setRangoMaximo(elrango);
	};

	useEffect(() => {
		eselrangoMaximo();
	}, []);

	return (
		<div className="App">
			<header className="App-header">
				<section>
					<input className="algo" type="text" placeholder="Aca aparecera la contraseÃ±a" />
					<div className="restricciones">
						<label htmlFor="range">
							Rango (
							<span>{rangoMaximo}</span>
							)
							<input id="range" className="range" type="range" max="20" onChange={eselrangoMaximo} />
						</label>
						<label htmlFor="num">
							Numeros
							<input id="num" className="num" type="checkbox" />
						</label>
						<label htmlFor="char">
							Char
							<input id="char" className="char" type="checkbox" />
						</label>
						<label htmlFor="caps">
							Caps
							<input id="caps" className="caps" type="checkbox" />
						</label>
					</div>
					<Button texto="Generar" onClick={verificarRestricciones} isotipo />
				</section>
			</header>
		</div>
	);
}

export default App;
